import React from "react";
import "./App.css";
import { useState } from "react";

const App = () => {
  const [text, setText] = useState("test quote");
  const [author, setAuthor] = useState("test author");

  var unirest = require("unirest");
  var req = unirest(
    "GET",
    "https://andruxnet-random-famous-quotes.p.rapidapi.com/"
  );

  req.query({
    cat: "famous",
    count: "1",
  });

  req.headers({
    "x-rapidapi-host": "andruxnet-random-famous-quotes.p.rapidapi.com",
    "x-rapidapi-key": "SIGN-UP-FOR-KEY",
    useQueryString: true,
  });

  req.end(function (res) {
    if (res.error) throw new Error(res.error);
    setText(res.body.quote);
    setAuthor(res.body.author);
  });

  req.end(function (res) {
    if (res.error) throw new Error(res.error);
    setText(res.body.quote);
    setAuthor(res.body.author);
  });

  const getNewQuote = () => {
    console.log("new quote");
  };

  return (
    <div className="app">
      <div className="app__quote" id="quote-box">
        <h3 className="quote__text" id="text">
          {`"${text}"`}
          <span className="quote__author" id="author">
            {` -${author}`}
          </span>
        </h3>

        <div className="quote__controls">
          <button
            className="quote__newQuoteButton"
            id="new-quote"
            onClick={getNewQuote}
          >
            New quote
          </button>
          <a href="" className="quote__tweetQuote" id="tweet-quote">
            tweet quote
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
