import React from "react";
import "./App.css";
import { useState } from "react";

const App = () => {
  const [text, setText] = useState("test quote");
  const [author, setAuthor] = useState("test author");

  const getNewQuote = (e) => {
    fetch("https://quotes.rest/").then((res) => {
      console.log(res);
    });
  };

  return (
    <div className="app">
      <div className="app__quote" id="quote-box">
        <h3 className="quote__text" id="text">
          {`"${text}"`}
          <span className="quote__author" id="author">
            {` -${author}`}
          </span>
        </h3>

        <div className="quote__controls">
          <button
            className="quote__newQuoteButton"
            id="new-quote"
            onClick={getNewQuote}
          >
            New quote
          </button>
          <a href="" className="quote__tweetQuote" id="tweet-quote">
            tweet quote
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
