import React from "react";
import "./App.css";
import { useState } from "react";
import { useEffect } from "react";

const App = () => {
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  useEffect(() => {
    fetch("https://type.fit/api/quotes")
      .then((res) => res.json())
      .then((json) => {
        let randomQuote = Math.floor(Math.random() * 1642);
        setText(json[randomQuote].text);
        setAuthor(json[randomQuote].author);
      });
  }, []);

  const getNewQuote = () => {
    fetch("https://type.fit/api/quotes")
      .then((res) => res.json())
      .then((json) => {
        let randomQuote = Math.floor(Math.random() * 1642);
        setText(json[randomQuote].text);
        setAuthor(json[randomQuote].author);
      });
  };

  return (
    <div className="app">
      <div className="app__quote" id="quote-box">
        <h3 className="quote__text" id="text">
          {`"${text}"`}
          <span className="quote__author" id="author">
            {author !== null ? ` ${author}` : ""}
          </span>
        </h3>

        <div className="quote__controls">
          <button
            className="quote__newQuoteButton"
            id="new-quote"
            onClick={getNewQuote}
          >
            New quote
          </button>
          <a
            className="twitter-share-button"
            id="tweet-quote"
            href={`https://twitter.com/intent/tweet?text=${text} - ${author}`}
            target="_blank"
          >
            Tweet
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
