import React from "react";
import "./App.css";
import { useState } from "react";
import { useEffect } from "react";

const App = () => {
  const [quotes, setQuotes] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  let randomQuote = Math.floor(Math.random() * 1642);

  useEffect(() => {
    fetch("https://type.fit/api/quotes")
      .then((res) => res.json())
      .then((json) => {
        setQuotes(json);
        setIsLoaded(true);
      });

    if (isLoaded === true) {
      setText(quotes[randomQuote].text);
      setAuthor(quotes[randomQuote].author);
    }
  }, [isLoaded]);

  const getNewQuote = () => {
    setText(quotes[randomQuote].text);
    setAuthor(quotes[randomQuote].author);
  };

  return (
    <div className="app">
      <div className="app__quote" id="quote-box">
        <h3 className="quote__text" id="text">
          {`"${text}"`}
          <span className="quote__author" id="author">
            {author !== null ? ` ${author}` : ""}
          </span>
        </h3>

        <div className="quote__controls">
          <button
            className="quote__newQuoteButton"
            id="new-quote"
            onClick={getNewQuote}
          >
            New quote
          </button>

          <a
            className="twitter-share-button"
            id="tweet-quote"
            href={`https://twitter.com/intent/tweet?text=${text} ${author}`}
            target="_blank"
          >
            Tweet <i class="fab fa-twitter"></i>
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
