import React from "react";
import "./App.css";
import { useState } from "react";

const App = () => {
  const [text, setText] = useState("test quote");
  const [author, setAuthor] = useState("test author");

  const getNewQuote = (e) => {
    function get_quote_of_the_day() {
      var xhttp = new XMLHttpRequest();
      xhttp.onreadystatechange = function () {
        if (this.readyState == 4 && this.status == 200) {
          alert(this.responseText);
        }
      };
      xhttp.open("GET", "https://quotes.rest/qod?category=inspire", true);
      xhttp.setRequestHeader("Content-type", "application/json");
      xhttp.setRequestHeader("X-Theysaidso-Api-Secret", "YOUR API HERE");
      xhttp.send();
    }
  };

  return (
    <div className="app">
      <div className="app__quote" id="quote-box">
        <h3 className="quote__text" id="text">
          {`"${text}"`}
          <span className="quote__author" id="author">
            {` -${author}`}
          </span>
        </h3>

        <div className="quote__controls">
          <button
            className="quote__newQuoteButton"
            id="new-quote"
            onClick={getNewQuote}
          >
            New quote
          </button>
          <a href="" className="quote__tweetQuote" id="tweet-quote">
            tweet quote
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
