import React from "react";
import "./App.css";
import { useState } from "react";
import { useEffect } from "react";

const App = () => {
  const [text, setText] = useState("test quote");
  const [author, setAuthor] = useState("test author");

  useEffect(() => {
    fetch("https://type.fit/api/quotes")
      .then((res) => res.json())
      .then((json) => {
        let randomQuote = Math.floor(Math.random() * 1642);
        console.log(randomQuote);
      });
  }, []);

  return (
    <div className="app">
      <div className="app__quote" id="quote-box">
        <h3 className="quote__text" id="text">
          {`"${text}"`}
          <span className="quote__author" id="author">
            {` -${author}`}
          </span>
        </h3>

        <div className="quote__controls">
          <button
            className="quote__newQuoteButton"
            id="new-quote"
            onClick={console.log("test")}
          >
            New quote
          </button>
          <a href="" className="quote__tweetQuote" id="tweet-quote">
            tweet quote
          </a>
        </div>
      </div>
    </div>
  );
};

export default App;
